<?php$cache_file = "subject.json";    header('Content-Type: text/javascript; charset=utf8');?>var subjectList = <?php echo file_get_contents($cache_file); ?> ; APchange = function(event, ui){	$(this).data("autocomplete").menu.activeMenu.children(":first-child").trigger("click");}function load_chapter(){	$( ".subjectSelection" ).autocomplete({		source: subjectList,		delay:300,		focus: function(event, ui) {			$(this).parent().find(".subject_hidden_value").val(ui.item.value);			$(this).val(ui.item.label);			return false;		},		select: function(event, ui) {							$(this).parent().find(".subject_hidden_value").val(ui.item.value);			$(this).val(ui.item.label);			// Ajax			var subject_id=ui.item.value;			var dataString = 'subject_id='+ subject_id;			var base_url = $('.baseUrl').val();			//alert(dataString);			$.ajax({				type: "POST",				url: base_url+"admin/model_test/load_chapter_questions",				data: dataString,				cache: false,				dataType: 'json',				success: function(json)				{					$("#chapter-question").html(json['chapter_question']);									} 			});									//-------------			$(this).unbind("change");			return false;		}	});	$( ".subjectSelection" ).focus(function(){		$(this).change(APchange);	});}